mapper.select:
SELECT DISTINCT issue.id as issue_id, issue.title as issue_title, issue.content as issue_content, issue.is_open as issue_is_open, issue.create_at as issue_create_at, author.id as author_id, author.nickname as author_nickname, author.profile_image_url as author_profile_image_url, milestone.id as milestone_id, milestone.title as milestone_title, label.id as label_id, label.title as label_title, label.color as label_color, label.description as label_description FROM issue INNER JOIN member author ON author.id = issue.author_id LEFT OUTER JOIN milestone ON issue.milestone_id = milestone.id AND milestone.is_deleted = 0 LEFT OUTER JOIN assigned_label ON issue.id = assigned_label.issue_id LEFT OUTER JOIN label ON assigned_label.label_id = label.id AND label.is_deleted = 0 LEFT OUTER JOIN assignee ON issue.id = assignee.issue_id LEFT OUTER JOIN member ON assignee.member_id = member.id LEFT OUTER JOIN issue_comment ON issue.id = issue_comment.issue_id AND issue_comment.is_deleted = 0 WHERE issue.is_deleted = 0
issue.is_open = #{isOpen}
issue.id IN ( SELECT issue_id FROM assignee WHERE member_id IN
#{assigneeId}
GROUP BY issue_id HAVING COUNT(DISTINCT member_id) = ${assigneeIds.size} )
issue.id IN ( SELECT issue_id FROM assigned_label WHERE label_id IN
#{labelId}
GROUP BY issue_id HAVING COUNT(DISTINCT label_id) = ${labelIds.size} )
issue.milestone_id = #{milestoneId}
issue.author_id = #{authorId}
issue_comment.author_id = #{commentAuthorId}
ORDER BY issue.id desc, label.id
