mapper.select:
SELECT * FROM ( SELECT DISTINCT issue.id as issue_id, issue.is_open as issue_is_open, issue.title as issue_title, issue.created_at as issue_created_at, milestone.name as milestone_name, author.id as author_id, author.login_id as author_login_id, author.profile_url as author_profile_url, label.id as label_id, label.name as label_name, label.font_color as label_font_color, label.background_color as label_background_color, assignee.id as assignee_id, assignee.login_id as assignee_login_id, assignee.profile_url as assignee_profile_url, DENSE_RANK() OVER (ORDER BY issue.id DESC) as row_num FROM issue LEFT JOIN issue_label ON issue.id = issue_label.issue_id LEFT JOIN label ON label.id = issue_label.label_id AND label.is_deleted = false LEFT JOIN issue_assignee ON issue.id = issue_assignee.issue_id LEFT JOIN user_account assignee ON assignee.id = issue_assignee.user_account_id AND assignee.is_deleted = false LEFT JOIN milestone ON issue.milestone_id = milestone.id AND milestone.is_deleted = false JOIN user_account author ON author.id = issue.user_account_id AND author.is_deleted = false
AND issue.is_open = #{issueSearch.isOpen}
AND milestone.name = #{issueSearch.milestoneName}
AND issue.id IN ( SELECT issue_id FROM user_account JOIN comment ON user_account.id = comment.user_account_id AND comment.is_deleted = FALSE WHERE login_id = #{issueSearch.commenter} )
AND label.name IN
#{labelName}
AND assignee.login_id IN
#{assigneeName}
AND label.id IS NULL
AND milestone.id IS NULL
AND assignee.id IS NULL
) as result WHERE result.row_num BETWEEN #{offset} + 1 AND #{offset} + #{size};

SELECT COUNT(DISTINCT issue.id) FROM issue
LEFT JOIN issue_label ON issue.id = issue_label.issue_id LEFT JOIN label ON label.id = issue_label.label_id AND label.is_deleted = false LEFT JOIN issue_assignee ON issue.id = issue_assignee.issue_id LEFT JOIN user_account assignee ON assignee.id = issue_assignee.user_account_id AND assignee.is_deleted = false LEFT JOIN milestone ON issue.milestone_id = milestone.id AND milestone.is_deleted = false JOIN user_account author ON author.id = issue.user_account_id AND author.is_deleted = false
WHERE issue.is_deleted = FALSE
AND issue.is_open = #{issueSearch.isOpen}
AND milestone.name = #{issueSearch.milestoneName}
AND issue.id IN ( SELECT issue_id FROM user_account JOIN comment ON user_account.id = comment.user_account_id AND comment.is_deleted = FALSE WHERE login_id = #{issueSearch.commenter} )
AND label.name IN
#{labelName}
AND assignee.login_id IN
#{assigneeName}
AND label.id IS NULL
AND milestone.id IS NULL
AND assignee.id IS NULL
