mapper.select:
SELECT DISTINCT i.issue_id AS issue_id, i.title AS issue_title, i.status AS issue_status, i.created_at AS issue_created_at, author.login_id AS author_login_id, assignee_user.user_id AS assignee_user_id, assignee_user.image AS assignee_image, l.label_id AS label_id, l.name AS label_name, l.text_color AS label_text_color, l.background_color AS label_background_color, mis.milestone_id AS milestone_id, mis.name AS milestone_name FROM issue i INNER JOIN user AS author ON author.user_id = i.author_id LEFT OUTER JOIN milestone AS mis ON i.milestone_id = mis.milestone_id AND mis.status != 'deleted' LEFT OUTER JOIN issue_label il ON i.issue_id = il.issue_id LEFT OUTER JOIN label l ON il.label_id = l.label_id AND l.is_deleted = false LEFT OUTER JOIN assignee a ON i.issue_id = a.issue_id LEFT OUTER JOIN user assignee_user ON a.user_id = assignee_user.user_id LEFT OUTER JOIN comment c ON i.issue_id = c.issue_id AND c.is_deleted = false LEFT OUTER JOIN user comment_user ON c.author_id = comment_user.user_id
i.status != 'deleted'
i.status = 'open'
i.status = #{status}
author.login_id = #{authorName}
i.issue_id IN ( SELECT issue_id FROM issue_label JOIN label ON issue_label.label_id = label.label_id WHERE label.name = #{labelName} )
mis.name = #{milestoneName}
i.issue_id IN ( SELECT issue_id FROM assignee JOIN user ON assignee.user_id = user.user_id WHERE user.login_id = #{assigneeName} )
comment_user.login_id = #{commentAuthorName}
i.title LIKE CONCAT('%', #{title}, '%')

SELECT (SELECT COUNT(*) FROM label WHERE is_deleted = false) AS label_count, (SELECT COUNT(*) FROM milestone WHERE status != 'deleted') AS milestone_count, COUNT(DISTINCT CASE WHEN i.status = 'open' THEN i.issue_id END) AS open_issue_count, COUNT(DISTINCT CASE WHEN i.status = 'closed' THEN i.issue_id END) AS closed_issue_count FROM issue i INNER JOIN user AS author ON author.user_id = i.author_id LEFT OUTER JOIN milestone AS mis ON i.milestone_id = mis.milestone_id AND mis.status != 'deleted' LEFT OUTER JOIN issue_label il ON i.issue_id = il.issue_id LEFT OUTER JOIN label l ON il.label_id = l.label_id AND l.is_deleted = false LEFT OUTER JOIN assignee a ON i.issue_id = a.issue_id LEFT OUTER JOIN user assignee_user ON a.user_id = assignee_user.user_id LEFT OUTER JOIN comment c ON i.issue_id = c.issue_id AND c.is_deleted = false LEFT OUTER JOIN user comment_user ON c.author_id = comment_user.user_id
i.status != 'deleted'
author.login_id = #{authorName}
l.name = #{labelName}
mis.name = #{milestoneName}
assignee_user.login_id = #{assigneeName}
comment_user.login_id = #{commentAuthorName}
i.title LIKE CONCAT('%', #{title}, '%')
